ZZ divis(ZZ a, ZZ n)
{
    ZZ q, r;

    if (a > 0)
    {
        q = a / n;
        r = a - q * n;
    }

    else
    {
        q = a / n;
        r = (q * n) - a;
        r = n - r;
        q = q - 1;
    }


    return r;
}



ZZ fuerza_bruta(ZZ m, ZZ n)
{
  int vueltas=0;
  ZZ i=m;
  while(divis(m,i)!=0||divis(n,i)!=0)
  {
      vueltas++;
    i=i-1;
    //cout<<"mcd: "<<i<<endl;
  }
  //cout<<"vueltas:"<<vueltas<<endl;
  return i;

}




ZZ euclides_ext(ZZ a, ZZ b)
{
    ZZ r1,r2,s1,s2,t1,t2,s,t;

	 r1 = a, r2 = b,
	 s1 = 1, s2 = 0;
	 t1 = 0, t2 = 1;
	 s;
	 t;
	while (r2 > 0)
	{
		ZZ q = r1 / r2;
		ZZ r = r1 - (q * r2);
		r1 = r2; r2 = r;

		s = s1 - (q * s2);
		s1 = s2; s2 = s;

		t = t1 - (q * t2);
		t1 = t2; t2 = t;
	};
	s = s1;
	if (s < 0)
		s = divis(s, b);
	return r1;
}

ZZ euclides(ZZ a, ZZ b) {
	if (a == 0)
 		return b;
 	return euclides(divis(b, a), a);
}

ZZ binary(ZZ a, ZZ b) {
  if (a == 0)
	return b;
  if (b == 0)
	return a;

  int k;
  for (k = 0; ((a | b) & 1) == 0; ++k) {
	a >>= 1;
	b >>= 1;
  }

  while ((a & 1) == 0)
	a >>= 1;

  do {
	while ((b & 1) == 0)
  	b >>= 1;

	if (a > b)
  	swap(a, b);

	b = (b - a);
  } while (b != 0);
  a = a << k;
  return a;
}

ZZ gcd(ZZ a, ZZ b)
{
    if (a == 0)
    {return b;}
    return gcd(divis(b,a), a);
}

